mapper.insert:
insert into areaprice (areaPriceid, price, floorid
  )
values (#{areapriceid,jdbcType=INTEGER}, #{price,jdbcType=VARCHAR}, #{floorid,jdbcType=INTEGER}
  )

mapper.update:
