set price = #{price,jdbcType=VARCHAR},
  floorid = #{floorid,jdbcType=INTEGER}
where areaPriceid = #{areapriceid,jdbcType=INTEGER}

mapper.select:
select areaPriceid, price, floorid
from areaprice
where areaPriceid = #{areapriceid,jdbcType=INTEGER}
